# 将逗号分隔的字符串转换为数组，并去除双引号
IFS=',' read -r -a debs <<< "$debs_str"
for i in "${!debs[@]}"; do
    debs[$i]="${debs[$i]//\"/}"
done

# 将 %2B 替换为 +
deb_packages=()
for deb in "${debs[@]}"; do
    deb_packages+=("${deb//%2B/+}")
done

# 获取Ubuntu版本
get_ubuntu_version() {
    awk -F'=' '/^VERSION_ID=/ {print $2}' /etc/os-release | tr -d '"'
}

version_id=$(get_ubuntu_version)
current_version="ubuntu-$(get_ubuntu_version)"

# 检查 version_id 是否为 18.04、20.04 或 22.04
if [[ "$version_id" == "18.04" || "$version_id" == "20.04" || "$version_id" == "22.04" ]]; then
    echo "版本号 $version_id 是有效的。"
else
    echo "版本号 $version_id 无效。"
    exit 1
fi

# 创建临时目录
temp_dir="/tmp/install_$app_name"
mkdir -p "$temp_dir"
cd "$temp_dir"

# 下载并安装 .deb 包
for deb in "${deb_packages[@]}"; do
    if [[ ! $deb == $current_version* ]]; then
        continue
    fi
    url="http://10.127.20.218:8003/offline_installer_packer/$deb"
    echo "Downloading $url"
    wget "$url" -O "$deb"
done

# 安装所有下载的 .deb 包
sudo dpkg -i --force-all *.deb

echo "Installation completed."